def extractKeyword(text):
    #extã‚’å½¢æ…‹ç´ è§£æžã—ã¦ã€åè©žã®ã¿ã®ãƒªã‚¹ãƒˆã‚’è¿”ã™"""
    tagger = MeCab.Tagger()
    encoded_text = text.encode('utf-8')
    tagger.parse('')    #python3ã®ãƒã‚°å›žé¿ç”¨ã€€cf http://www.trifields.jp/how-to-use-mecab-in-ubuntu-14-04-and-python-3-1196
    node = tagger.parseToNode(text)
    wordCount = {}
    keywords = []
    while node:
        hinshi = node.feature.split(",")[0]
#         print(hinshi)
        if hinshi  == "å‹•è©ž" or hinshi == "åè©ž":
            keywords.append(node.surface)
            wordCount.setdefault(node.surface,0)
            wordCount[node.surface]+=1
        node = node.next
    return keywords, wordCount
