def tfdtf(allwords , texts):
    #å…¨æ–‡æ›¸æ•°
    D = len(texts)
    print(D)
    #idf
    idfs = {}
    for word in allwords:
        count = 0 
        for text in texts:
            if word in text:
                count += 1
        idf = math.log10((D/count))
        idfs[word] = str(idf)
    print(idfs)
    #TF
    list_tfs = []
    for text in texts:
        tfs = {}
        sigma = len(text)
#         print(text)
        for word in text:
            tf = (text[word]/sigma)
            tfs[word] = tf
#         print(tfs)
        list_tfs.append(tfs)
    #TF-IDF
    tfidf_list = []
    for tfs in list_tfs:
        tfidf_line = {}  
        for tf in tfs:
            cidf = float(idfs[tf])
            ctf = float(tfs[tf])
            ctfidf = cidf * ctf
            tfidf_line[tf] = ctfidf
        tfidf_list.append(tfidf_line)

    return(tfidf_list)
        
    
           
        
    
